func:
msg.OPENAI_API_KEY = msg.payload.openAIKey;
msg.model = msg.payload.model;

msg.messages= [
    {
        role: "system",
        content: "act as a world class marketer",
    },
    {
        role: "user", content: `
        Product: ${msg.payload.product}
        Description: ${msg.payload.description}
        Tone of voice: ${msg.payload.voice}

        Write a before-after-bridge for the above:` 
    },
];



return msg;

if(msg.statusCode === 200){
    const answers = msg.payload.choices[0].message.content
    msg.payload= answers;
}
else {
    msg.payload = `     Ooops, something went wrong!ðŸ¥²
    ${msg.payload.error.message}`
}

return msg;

